import React from 'react';
import './styles.css';

import { CarteContent } from '../../../../data/site/carteContent';
import Grid from '@material-ui/core/Grid';


const CartePageMobile = (props) => {
    const idMenu = props.idMenuPageMobile;

    return (
        <div id="menu-page-mobile">
                {/* If idMenu is Entree version mobile */}
                { idMenu===1 && CarteContent.map((idContent) => {
                      if(idContent.id === idMenu) {
                        return (
                        <div className="menu-item-entree">
                          <h4>Entr√©es</h4>
                          <div className="menu-entree-text">
                          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Amet facilisis auctor malesuada cras viverra amet in lectus. </p>

                          {idContent.data.map((data) => (
                              <Grid container  className="item-entree">
                                <Grid container direction="column" alignItems="flex-start" xs={9}>

                                      <h4>{data.name}..........................</h4>
                                      <p> {data.text} </p>


                                </Grid>
                                <Grid container justifyContent="flex-end" xs={3}>
                                  <h4>{data.prix}</h4>
                                </Grid>
                              </Grid>                          
                        ))}
                          </div>
                        </div>
                        )
                      } else return null;
                    })}

          {/* When idMenu is not Entre (1) version Mobile */}
                    { idMenu!==1 && CarteContent.map((idContent) => {
                      if(idContent.id === idMenu) {
                        return (
                          idContent.data.map((data) => (
                            <div className="menu-item-mobile">

                                <Grid container direction="row" xs={12} >
                                  <Grid container alignItems="center" justifyContent="center" direction="column"  xs={4}>
                                    <img src={data.photo} alt={data.name}></img>
                                  </Grid>
                                  <Grid container justifyContent="center" alignItems="flex-start" direction="column" xs={8}>
                                    <div className="menu-item-info-mobile">
                                      <p className="menu-item-titre-mobile">{data.name}</p>
                                      <p> {data.text} </p>
                                      <p><b>{data.prix} </b> </p>
                                    </div>

                                  </Grid>
                                </Grid>

                            </div>
                        ))
                        )
                      } else return null;
                    })}
        </div>
    );
};

export default CartePageMobile;
