                                <p onClick={() => setShowJusFruit(!showJusFruit)}>
                                  <ExpandedIcon expanded={showJusFruit} setExpanded={setShowJusFruit} />
                                </p>
                              </div>

                            {props.ingredients?.map((data) => (
                              <>
