                                        <FormControlLabel
                                          value={data.nom}
                                          control={<Radio />}
                                          id={data.id}
                                          className="radio-choice__menu"
                                          onClick={() => {
                                            setError(false);
                                            setPrixTotal(props.detail.prix);
                                            //setTailleSelected(data);
                                          }}
                                        />
                                        <span>{data.nom}</span>
                                      </div>
                                    :null}
                                </>

                              ))}

                              <div className="boisson_choice_container">
