              :null}


              {/****************************** ENTREES *******************************/}

                  {props.detail.categorie === 2 &&
                   props.detail.nom.includes('5') ||
                   props.detail.nom.includes('10') ?

                    <>


                    <Modal.Body style={!props.detail.disponibilite ? ({display: "none"}) : ({display: "block"})}>
                        <Modal.Body.Heading
                          style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
