                            name="sauce"
                            value={sauceSelected}
                            onChange={handleSauceSelected}
                          >

                            {props.ingredients?.map((data) => (
                              <>
                                  {data.type_ingredient === "sauce" ?
                                      <div className="sides-radioGroup" key={data.id}>
                                        <FormControlLabel
                                          value={data.nom}
                                          control={<Radio />}
                                          id={data.id}
                                          className="radio-choice__menu"
                                          onClick={() => {
