                                      className="radio-choice__menu"
                                      onClick={() => {
                                        setError(false);
                                        setAccompgnementSuppTabSelected(data);
                                        setPrixTotal(props.detail.prix + data.prix);
                                        console.log(data);
                                      }}
                                    />
                                    <span>
                                      {data.nom + " (+ " + data.prix.toFixed(2) + "€)"} 
