                                <div className="sides-radioGroup" key={1}>
                                  <FormControlLabel
                                    value="moyenne"
                                    control={<Radio />}
                                    id={1}
                                    className="radio-choice__menu"
                                    onClick={() => {
                                      setError(false);
                                      setPrixTotal(props.detail.moyen);
                                    }}
                                  />
