                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
