                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_freez_boisson_gazeuse && data.type_ingredient === "freez_boisson_gazeuse" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_ode_bio && data.type_ingredient === "ode_bio" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_limonaide_bio && data.type_ingredient === "est_limonaide_bio" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_milkshake && data.type_ingredient === "est_milkshake" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_smoothie && data.type_ingredient === "est_smoothie" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}

                                  </>

                                ))}

                            </RadioGroup>
                          </FormControl>
                          {error && (
                            <p className="error">Veuillez sélectionner au moins un élément</p>
                          )}
