import { useState, useEffect } from "react";

import {
  Button,
  FormControl,
  IconButton,
  Input,
  InputLabel,
  makeStyles,
  Select,
  MenuItem,
  // Modal,
} from "@material-ui/core";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import useForm from "../../../hooks/useForm";

import "./paiement.css";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import { sendrequest } from "../../../middlewares/request";

import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { useSelector, useDispatch } from "react-redux";
import { selectBaskets, emptyBasket } from "../../../app/Redux-slices/basketsSlice";
//import spinner from "../images/spinner.gif";
import { calculPrixProduitAvecQuantite, calculTotal } from "../../../utilities";
import { useHistory } from "react-router-dom";
import { useLocation } from "react-router-dom";
import axios from "axios";
import Modal from "../Carte/MyModal/Modal";
import { URL } from "../../../middlewares/request";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
  margin: {
    margin: theme.spacing(1, 0),
  },
  marginLeftRight: {
    marginRight: theme.spacing(1),
    marginLeft: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
  textField: {
    width: "25ch",
  },

  buttonIcon: {
    paddingLeft: 1,
  },

  border: {
    "& label.Mui-focused": {
      color: "#ccc",
    },
    "& .MuiInput-underline:after": {
      borderBottomColor: "#ccc",
    },

    "& .MuiOutlinedInput-root": {
      "&:hover fieldset": {
        borderColor: "#ccc",
      },
      "&.Mui-focused fieldset": {
        borderColor: "#ccc",
      },
    },
  },
}));

// variable arbitraire pour effectuer des tests
const initial = {
  nom: "",
  prenom: "",
  email: "",
  num_tel: "",
  adresse: "",
  ville: "",
  code_postale: "",
  montant: 0,
};


const Paiement = (props) => {
  // With location we can retrieve the kind of delivery chosen (was passed in props of history)
  const [paiment_process, setPaiement_process] = useState("livraison");
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null);
  const [disabled, setDisabled] = useState(true);
  const [processing, setProcessing] = useState(false);
  const [succeeded, setSucceeded] = useState(false);
  // const [supplements, setSupplements] = useState([]);


  const location = useLocation();
  const stripe = useStripe();
  const elements = useElements();
  const history = useHistory();
  const dispatch = useDispatch();
  const baskets = useSelector(selectBaskets);
  const classes = useStyles();



  const handleChange = (event) => {
    // Listen for changes in CardElement
    // and display any errors as the customer types their card details
    setDisabled(event.empty);
    setError(event.error ? event.error.message : "");
  };

  // Fonction liée au paiement ainsi qu'à l'enregistrement des infos du clients
  const Payer = async (e) => {
    e.preventDefault();

    setProcessing(true);

    const { data } = await axios.post(URL + "paiement/create-client-secret", {
      amount: 1000,//parseInt((prixTotal * 100).toFixed(2), 10),
      //email,
    });
    // *100 : stripe prend l'unité en centimes
    // 3€ -> 300 centimes


    if(CardElement !== null){
      console.log(CardElement)
      console.log(elements)
      console.log(elements.getElement("card"))
    await stripe
      .confirmCardPayment(data.clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        },
      })
      .then((res) => {
        setError(null);
        setProcessing(false);
        setDisabled(false);

      })
      .catch((err) => {
        console.log(err.message);
        setError(`Paiement échoué: ${err.message}`);
        setProcessing(false);
        //setSuccess(false);

      });
     console.log("OK") 
    }


    };


  const handleClose = (e) => {
    if (
      e.target.classList.contains("myModal__backdrop") ||
      e.target.classList.contains("myModal__modal__close-btn") ||
      e.target.parentNode.classList.contains("myModal__modal__close-btn") ||
      e.target.parentNode.parentNode.classList.contains(
        "myModal__modal__close-btn"
      )
    ) {
      setShowModal(false);
    }
  };

  const handleOpen = () => {
    setShowModal(true);
  };

  useEffect(() => {
    console.log(`baskets de paiement à linterieru`, baskets);
  }, [baskets]);

  return (
    <div className="paiment">
      <div className="paiement__container">
              <form className="paiement__form">
                <CardElement
                  id="card-element"
                  onChange={handleChange}
                  options={cardStyle}
                />
                {error && (
                  <div className="paiement__details__error">* {error}</div>
                )}

                {baskets.length === 0 && (
                  <div className="paiement__details__error">
                    * Vous n'avez rien dans le panier
                  </div>
                )}

                <Button
                  onClick={Payer}
                  disabled={
                    prixTotal <= 1 || processing || disabled || succeeded
                  }
                  type="submit"
                  variant="contained"
                  color="primary"
                  className="paiement--btn-payer"
                >
                  <span className="paiement--btn-payer-text">
                    {processing ? (
                     {/* <img src={spinner} alt="spinner" /> */}
                    ) : (
                      <>Payer {prixTotal}€</>
                    )}
                  </span>
                </Button>
              </form>

      </div>
    </div>
  );
};

const cardStyle = {
  style: {
    base: {
      color: "#32325d",
      fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
      fontSmoothing: "antialiased",
      fontSize: "16px",
      "::placeholder": {
        color: "#aaa",
        backgroundColor: "#fff",
      },
      ":-webkit-autofill": {
        color: "#fff",
      },
      ":focus": {
        color: "#aaa",
      },
    },

    invalid: {
      color: "#fa755a",
      iconColor: "#fa755a",
    },
  },
  hidePostalCode: true,
};


export const Wrapper = (props) => {

  //const config = useSelector(state => state.config.info);
  //const stripe = window.Stripe(config.stripe);

  return (
    <Elements stripe={loadStripe("pk_test_51JQ9d2G8uFuhwMtfeIIgrp9mSsWPuVqmTeqZVNc58ukTd42QqUEsnWf8vXIXS5q6Mu6bKS2PfElbRySFMeMHTaQK00WyHNKcuX")}>
      <Paiement />
    </Elements>
  );
}

export default Wrapper;
