import { makeStyles } from "@material-ui/core";
import Contact from "./Contact";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "90%",
    margin: "3rem auto",
    alignItems: "center",
  },
  heading: {
    fontSize: theme.typography.pxToRem(16),
    fontWeight: theme.typography.fontWeightMedium,
    color: "black",
    margin: 0,
    //width: "20%",
  },

  heading_end: {
    fontSize: theme.typography.pxToRem(16),
    fontWeight: theme.typography.fontWeightMedium,
    color: "black",
    margin: 0,
    marginBottom: "10%",
    //width: "20%",
  },

  table: {
    width: "100%",
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
  },

  column: {
    display: "flex",
    flexDirection: "column",
  },

  detail: {
    fontSize: theme.typography.pxToRem(16),
    color: "black",
  },

  color: {
    color: "black",
  },
}));

const ListeContact = ({ contacts }) => {
  const classes = useStyles();

  return (
    <div className="text_contact">
      {contacts.length > 0 && (
        <>
          <div
            className={classes.table}
            style={{ padding: "0 1rem", borderBottom: "1px solid black" }}>
            <div className="resp_message_header">
            <p className={classes.heading}>Date</p>
            <p className={classes.heading_end}>Heure</p>
            </div>
            <div className="resp_message_header">
            <p className={classes.heading}>Nom</p>
            <p className={classes.heading_end}>Prénom</p>
            </div>
            <div className="resp_coordonnee">
            <p className={classes.heading}>Mail</p>
            <p className={classes.heading_end}>Téléphone</p>
            </div>
            <div className="resp_message_header">
            <p className={classes.heading}>Prestation</p>
            <p className={classes.heading_end}>Société</p>
            </div>
            <div className="resp_message">
            <p className={classes.heading_end}>Message</p>
            </div>
          </div>

          {contacts.map((contact) => (
            <Contact
              contact={contact}
              key={contact.id}
            />
          ))}
        </>
      )}
    </div>
  );
};

export default ListeContact;
