import React from 'react';
import "./styles.css";
import {Grid, makeStyles, TextField} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '90%',
      },
    },
  }));

const ContactFormCoord = () => {
    const classes = useStyles();

    return (
        <div className="contact-coord">
                        <h2>Entrez vos coordonnées</h2>
                        <form>
                        <Grid container direction="row">
                            <Grid  xs={12} md={6}>
                                <form className={classes.root} noValidate autoComplete="off">
                                    <TextField id="filled-basic" 
                                        label="Nom" required
                                        variant="filled" />
                                    <TextField id="filled-basic" 
                                        label="Prénom" required
                                        variant="filled" />
                                    <TextField id="filled-basic" 
                                        label="Société/Etablissement" 
                                        variant="filled" />
                                    <TextField id="filled-basic" 
                                        label="Votre mail"
                                        variant="filled" />
                                    <TextField id="filled-basic" 
                                        label="Téléphone" required
                                        variant="filled" />
                                    <TextField id="filled-basic" 
                                        label="Préstation" required
                                        variant="filled" />
                                </form>
                            </Grid>
                            <Grid xs={12} md={6}>
                                <form className={classes.root} noValidate autoComplete="off">
                                    <TextField fullWidth multiline 
                                        rows={18} id="filled-basic" 
                                        label="Message" 
                                        variant="filled" />
                                </form>
                            </Grid>
                        </Grid>
                        <button type="submit" className="btnEnvoyer">ENVOYER</button>
                        </form>
                    </div>
    );
}
export default ContactFormCoord;
