import { useEffect, useState } from "react";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Carte from "./Carte";
//import MenuType from "../../Menu/MenuType";
import { splitPrix } from "../../../utilities";
import Card from "./Carte/card";
import CartePage from '../../site/Carte/CartePage';
import AddProductModal from "./MyModal/AddProductModal";
import Modal from "./MyModal/Modal";
import "./index.css";
import axios from "axios";
import { IconButton, Button } from "@material-ui/core";
import { URL, sendrequest } from "../../../middlewares/request";

export default function GestionProduits () {
    const [active, setActive] = useState(1);
    const [activeCarte, setActiveCarte] = useState(true);
    const [datas, setDatas] = useState([]);
    const [sideDishes, setSideDishes] = useState([]);
    const [menuCategories, setMenuCategories] = useState([]);
    const [dishesDisplayed, setDishesDisplay] = useState(false);
    // Booleans that will only allow the component to render when the requests have returned the datas
    const [isDataLoading, setDataLoading] = useState(true);
    const [isCategoryLoading, setCategoryLoading] = useState(true);
    const [sideDishesLoading, setSideDishesLoading] = useState(false);
    const [show, setShow] = useState(false);
    const [showProductModal, setShowProductModal] = useState(false);
    // activeCarte && (window.document.body.style.overflow = "hidden")
    activeCarte
        ? (window.document.body.style.overflow = "hidden")
        : (window.document.body.style.overflow = "auto");
    const fetchData = async () => {
        /*sendrequest("get", "gonak/produit/", setDatas, setDataLoading);
        sendrequest(
            "get",
            "gonak/categorie/",
            setMenuCategories,
            setCategoryLoading
        );*/
        //sendrequest("get", "letexan/produit/?accompagnement=true", setSideDishes, setSideDishesLoading);
        axios.get(URL + "gonak/produit/").then((res) => {
          setDatas(res.data);
          console.log(res.data);
        });

        axios.get(URL + "gonak/categorie/").then((res) => {
          setMenuCategories(res.data);
          console.log(res.data);
        });
    };

    useEffect(() => {
        fetchData();
    }, []);

    //Function that will check through if id of the selected menu item matches the one of 'Menu'. If so then we will want to display all of the datas, not just a selection.
    const isMenu = () => {
        for (var i = 0; i < menuCategories.length; i++) {
            if (menuCategories[i].id === active && menuCategories[i].nom === "Menus du Midi") {
                return datas;
            }
        }
    };

  const handleClose = (e) => {

    if (
      e.target.classList.contains("myModal__backdrop") ||
      e.target.classList.contains("myModal__modal__close-btn") ||
      e.target.parentNode.classList.contains("myModal__modal__close-btn") ||
      e.target.parentNode.parentNode.classList.contains(
        "myModal__modal__close-btn"
      )
    ) {
      setShow(false);
      //setShowButton(false);
    }
  };

  const deleteProduct = (id) => {
    axios.delete(URL + "gonak/produit/" + id + "/")
      .then(() => console.log(`deleted`))
      .catch(error => console.error(error));
  }
  const updateProduct = () => {

  }

    const selectDishesPerCategory = () => {
        var menuDishes = isMenu();
        // !!!!!!!! A VOIR !!!!!!!!!!
        if (menuDishes !== undefined) {

        } else {
            const selectedDishes = datas
                // We filter the data :
                .filter((data) => {
                    // By only selecting the data that belongs to a category (categories is an array in which are the different categories to which a dish belongs) that matches the one selected by the user (var active)
                    return (
                        data.categorie === active
                    )


                    /*for (var i = 0; i < data.categories.length; i++) {
                      if (data.categories[i] === active) return true;
                      return false;
                    }*/
                })
                //Once filtered, we can go through the selection to display them
                .map((data) => {
                    return (
                        <>
                            <div style={{margin: "auto", width: "100%"}}onClick={() => setShow(true)}>
                                <CartePage 
                                    key={data.categorie} 
                                    idMenuPage={data.categorie} 
                                />
                            </div>

                            <Modal showModal={show} setShowModal={setShow} handleClose={handleClose}>
                              <Modal.Header>
                                <h1>{data.nom}</h1>
                                <img src={data.image} alt={data.nom} />
                                <p>{splitPrix(data.prix)}</p>
                              </Modal.Header>
                              <Modal.Body>
                                <Modal.Body.Heading>Description</Modal.Body.Heading>
                                <p>{data.description}</p>
                              </Modal.Body>
                              <Modal.Footer>
                                <div
                                  style={{
                                    marginRight: "15px",
                                    width: "100%",
                                    display: "flex",
                                    justifyContent: "space-between",
                                    border: "0.7px solid black",

                                  }}>
                                  <Button onClick={deleteProduct}>supprimer</Button>
                                  <Button onClick={setShowProductModal}>Modifier</Button>
                                  <AddProductModal 
                                    productToUpdate={data} 
                                    categorieId={data.id} 
                                    show={showProductModal} s
                                    etShow={setShowProductModal} ></AddProductModal>
                                </div>
                              </Modal.Footer>
                            </Modal>
                        </>
                    );
                });
            return selectedDishes;
        }
    };

    return (
        <div className='commander__'>
            <div style={{ textAlign: 'center', width: "100%" }}>
            {isCategoryLoading &&
                <FormControl >
                    <InputLabel id="demo-simple-select-label">Categorie</InputLabel>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={active}
                    onChange={(e)=>{
                        setActive(e.target.value);
                        setActiveCarte(false);
                        setDishesDisplay(false);
                    }}
                    >
                        {menuCategories.map(categorie=>{
                            return <MenuItem value={categorie.id}>{categorie.nom}</MenuItem>
                        })}
                    </Select>
                </FormControl>
            }
                <Button onClick={() => setShowProductModal(true)} > Ajouter Produit</Button>
                <AddProductModal categorieId={active} show={showProductModal} setShow={setShowProductModal} ></AddProductModal>
            </div>

            <div className='commander__container'>
                {isDataLoading && (
                    <>
                        <div className='commander__container__cards'>
                            {selectDishesPerCategory()}
                        </div>

                        <div
                            className={"commander__carte " + (activeCarte ? "white" : null)}>
                            <IconButton onClick={() => setActiveCarte(() => !activeCarte)}>
                                <i
                                    className={
                                        "fas fa-arrow-" + (activeCarte ? "left" : "right")
                                    }></i>
                            </IconButton>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};
