import React, {useState, useEffect} from 'react'
import './styles.css';
import axios from "axios";
import { URL, sendrequest } from "../../../../middlewares/request";
//import { CarteContent } from '../../../../data/site/carteContent';
import Grid from '@material-ui/core/Grid';
import ShowMoreText from "react-show-more-text";


const CartePage = (props) => {
  const idMenu = props.idMenuPage;
  const [produits, setProduits] = useState([]);
  const [show, setShow] = useState(false);

    const fetchProduit = () => {
      axios.get(URL + "gonak/produit/?categorie=" + idMenu).then((res) => {
        setProduits(res.data);
        console.log(res.data);
      });
    };

    useEffect(() => {
      fetchProduit();
      //console.log(produitsCarte);
    }, []);

    const showMultiplePrice = (data) => {

      if(data.categorie === 1 && data.prix === 0){
        return (<p><b>{"À partir de " + data.prix_gourmande + " €"} </b> </p>)
      }
      else if(data.categorie === 2 && data.prix === 0){
        return (<p><b>{"À partir de " + data.pieces_x5 + " €"} </b> </p>)
      }
      else if(data.categorie === 4 && data.prix === 0){
        return (<p><b>{"À partir de " + data.sans_accompagnement + " €"} </b> </p>)
      }
      else if(data.categorie === 12 && data.prix === 0){
        return (<p><b>{"À partir de " + data.moyen + " €"} </b> </p>)
      }
      else{
        return (<p><b>{data.prix + " €"} </b> </p>)
      }
    }

    return(
        <div id="menu-page">

                    { produits.map((data) => (
                            <div 
                              className="menu-item" 
                            >
                              <Grid container direction="row" >
                                <Grid container alignItems="center" justifyContent="center" direction="column"  xs={4}>
                                  <img src={data.image} alt={data.nom}></img>
                                </Grid>
                                <Grid container justifyContent="center" alignItems="flex-start" direction="column" xs={8}>
                                  <div className="menu-item-info">
                                    <p className="menu-item-titre">{data.nom}</p>
                                    <ShowMoreText
                                        /* Default options */
                                        lines={1}
                                        more="voir plus"
                                        less="voir moins"
                                        className="content-css"
                                        anchorClass="my-anchor-css-class"
                                        /*onClick={this.executeOnClick}*/
                                        expanded={false}
                                        width={280}
                                        truncatedEndingComponent={"... "}
                                        className="menu-item-text"
                                    >
                                        {data.description}
                                    </ShowMoreText>
                                  {/* Produit à prix multiple => Formules / Entrées / Rotisserie / Soft drinks */}
                                    {showMultiplePrice(data)}
                                  </div>

                                </Grid>
                              </Grid>

                              <Modal
                                  showModal={show}
                                  setShowModal={setShow}
                                  handleClose={handleClose}
                                >
                                  <Modal.Header>
                                    <h1>{data.nom}</h1>
                                    <img src={data.image} alt={data.nom} />
                                    {showMultiplePrice(data)}
                                  </Modal.Header>

                                  <Modal.Body>
                                    <Modal.Body.Heading>Description</Modal.Body.Heading>
                                    <p>{data.description}</p>
                                  </Modal.Body>

                                  {/*------------------------- Choix Pain ------------------------------------------*/}
                                  {data.est_menu_sandwichs || data.est_menu_sandwichs_au_four ?
                                    <>
                                      <Modal.Body.Heading
                                        style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                                      >
                                        Pain au choix
                                      </Modal.Body.Heading>
                                      <FormControl component="fieldset">
                                        <RadioGroup
                                          name="pain_sandwich"
                                          value={painSelected}
                                          onChange={handleChangePain}
                                        >
                                            <div className="sides-radioGroup" key={1}>
                                              <FormControlLabel
                                                value={"Gourmande"}
                                                control={<Radio />}
                                                id={1}
                                                className="radio-choice__menu"
                                                /*onClick={() => {

                                                }}*/
                                              />
                                              <Modal.Body.Heading>{"Gourmande"}</Modal.Body.Heading>
                                            </div>

                                            <div className="sides-radioGroup" key={1}>
                                              <FormControlLabel
                                                value={"Intense"}
                                                control={<Radio />}
                                                id={1}
                                                className="radio-choice__menu"
                                                /*onClick={() => {

                                                }}*/
                                              />
                                              <Modal.Body.Heading>{"Intense"}</Modal.Body.Heading>
                                            </div>

                                        </RadioGroup>
                                      </FormControl>
                                      {error && (
                                        <p className="error">Veuillez sélectionner au moins un élément</p>
                                      )}

                                      <div className="separation_ligne"> </div>
                                    </>
                                  :null}

                                  {/*------------------------- Choix Taille Pizza ------------------------------------------*/}
                                  {data.est_pizza_sauce_tomate || data.est_pizza_sauce_barbecue || data.est_pizza_creme_fraiche ?
                                    <>
                                      <Modal.Body.Heading
                                        style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                                      >
                                        Taille Pizza
                                      </Modal.Body.Heading>
                                      <FormControl component="fieldset">
                                        <RadioGroup>
                                          {choixTaille.map((data) => {
                                            if (1 === 1)
                                              return (
                                                <div className="sides-radioGroup" key={data.id}>
                                                  <FormControlLabel
                                                    value={data.nom}
                                                    control={
                                                      <Radio
                                                        onChange={(e) => {
                                                          setTailleSelected(e.target.value);
                                                          setPrixTailleSelected(data.prix);
                                                          setPrixTotal(data.prix);
                                                        }}
                                                      />
                                                    }
                                                    id={data.id}
                                                    className="radio-choice__menu"
                                                  />
                                                  {TailleSelected === data.nom &&
                                                  TailleSelected !== "Seul" ? (
                                                    <>
                                                      <div className="zones__container">
                                                        <FormControlLabel
                                                          className="radio__panier-zone"
                                                          control={<FormControl />}
                                                          label={freePizza()}
                                                        />
                                                      </div>
                                                    </>
                                                  ) : (
                                                    ""
                                                  )}
                                                  <Modal.Body.Heading>
                                                    {data.nom + " (" + data.prix.toFixed(2) + "€)"}
                                                  </Modal.Body.Heading>
                                                </div>
                                              );
                                            else return null;
                                          })}
                                        </RadioGroup>
                                      </FormControl>
                                      {error && (
                                        <p className="error">Veuillez sélectionner au moins un élément</p>
                                      )}

                                      <div className="separation_ligne"> </div>
                                    </>
                                  :null}


                                  {/*------------------------- Choix Sauce ------------------------------------------*/}
                                  {data.est_menu_sandwichs || data.est_menu_sandwichs_au_four || data.est_menu_burgers ||
                                   data.est_burger || data.est_assiette || data.est_menu_tacos || data.est_menu_croque || 
                                   data.est_menu_paninis || data.est_crepe_salee || data.est_panini || data.est_croque ?
                                    <>
                                      <Modal.Body.Heading
                                        style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                                      >
                                        Sauce(s)
                                      </Modal.Body.Heading>
                                      <FormControl component="fieldset">
                                        <FormGroup>
                                          {choixSauce.map((data) => {
                                            if (1 === 1)
                                              return (
                                                <div className="sides-radioGroup" key={data.id}>
                                                  <FormControlLabel
                                                    value={data.nom}
                                                    control={<Checkbox onChange={handleChangeSauce} />}
                                                    id={data.id}
                                                    className="radio-choice__menu"
                                                    onClick={() => {
                                                      //setError(false);
                                                      setSauceSelected(data);
                                                    }}
                                                  />
                                                  <Modal.Body.Heading>{data.nom}</Modal.Body.Heading>
                                                </div>
                                              );
                                            else return null;
                                          })}
                                        </FormGroup>
                                      </FormControl>
                                      {error && (
                                        <p className="error">Veuillez sélectionner au moins un élément</p>
                                      )}
                                      {errorDouble && (
                                        <p className="error">
                                          Veuillez sélectionner jusqu'à 2 éléments maximum
                                        </p>
                                      )}

                                       <div className="separation_ligne"> </div>
                                    </>
                                  :null}
                                  {/*------------------------- Choix Supplement ------------------------------------------*/}
                                  {data.est_menu_sandwichs || data.est_menu_sandwichs_au_four || data.est_menu_burgers ||
                                   data.est_menu_tacos || data.est_menu_paninis || data.est_assiette || data.est_panini ||
                                   data.est_menu_crepe_salee || data.est_burger || data.est_crepe_salee || data.est_salade_pate ||
                                   data.est_pizza_sauce_tomate || data.est_pizza_sauce_barbecue || data.est_pizza_creme_fraiche ||
                                   data.est_milkshake || data.est_smoothie || data.est_accompagnement ?
                                    <>
                                      <Modal.Body.Heading
                                        style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                                      >
                                        Supplement(s)
                                      </Modal.Body.Heading>
                                      <FormControl component="fieldset">
                                        <FormGroup>
                                          {printSupplement(data).map((data) => {
                                            if (1 === 1)
                                              return (
                                                <div className="sides-radioGroup" key={data.id}>
                                                  <FormControlLabel
                                                    value={info_supplement(data)}
                                                    control={<Checkbox onChange={handleChangeSupplement} />}
                                                    id={data.id}
                                                    className="radio-choice__menu"
                                                  />
                                                  <Modal.Body.Heading>
                                                    {data.nom} <span>({data.prix.toFixed(2)}€)</span>
                                                  </Modal.Body.Heading>
                                                </div>
                                              );
                                            else return null;
                                          })}
                                        </FormGroup>
                                      </FormControl>
                                      {errorSupplement && (
                                        <p className="error">
                                          Veuillez sélectionner jusqu'à 8 suppléments maximum
                                        </p>
                                      )}

                                      <div className="separation_ligne"> </div>
                                   </>
                                  :null}  


                                  {/*------------------------- Choix Boisson ------------------------------------------*/}

                                  <Modal.Body>
                                  {data.est_menu_sandwichs || data.est_menu_sandwichs_au_four || data.est_menu_burgers ||
                                   data.est_menu_crepe_salee || data.est_menu_tacos || data.est_menu_croque ||
                                   data.est_menu_paninis ?
                                    <>
                                        <Modal.Body.Heading style={{ marginBottom: "1.5rem" }}>
                                          Boisson
                                        </Modal.Body.Heading>
                                        <FormControl component="fieldset">
                                          <RadioGroup
                                            name="boisson"
                                            value={boissonSelected}
                                            onChange={handleChangeBoisson}
                                          >
                                            {sideDishes.map((sideDish) => {
                                              if (sideDish.disponibilite)
                                                return (
                                                  <div className="sides-radioGroup" key={sideDish.id}>
                                                    <FormControlLabel
                                                      value={sideDish.nom}
                                                      control={<Radio />}
                                                      id={sideDish.id}
                                                      className="radio-choice__menu"
                                                      onClick={() => {
                                                        //setError(false);
                                                        setBoissonSelected(sideDish);
                                                      }}
                                                    />
                                                    <Modal.Body.Heading>{sideDish.nom}</Modal.Body.Heading>
                                                  </div>
                                                );
                                              else return null;
                                            })}
                                          </RadioGroup>
                                        </FormControl>
                                        {error && (
                                          <p className="error">Veuillez sélectionner au moins un élément</p>
                                        )}
                                        {errorUnique && (
                                          <p className="error">
                                            Veuillez sélectionner jusqu'à 1 élément maximum
                                          </p>
                                        )}
                                      </>
                                    :null}

                                    <TextField
                                      id="outlined-full-width"
                                      style={{ marginBottom: "2rem" }}
                                      placeholder="Informations importantes"
                                      helperText="Indiquez ici toute information importante"
                                      fullWidth
                                      margin="normal"
                                      rows={2}
                                      variant="outlined"
                                      onChange={handleCommentChange}
                                    />
                                  </Modal.Body>


                                  <Modal.Footer className="card__item__total__price">
                                    <Modal.Body.Heading>
                                      Prix Total
                                      <span style={{ fontSize: "1rem", color: "black" }}>(TTC)</span>
                                    </Modal.Body.Heading>
                                    <p style={{ fontSize: "1.3rem" }}>
                                      {(quantite * prixTotal).toFixed(2)}€
                                    </p>
                                  </Modal.Footer>
                                  <Modal.Footer>
                                    <div
                                      style={{
                                        marginRight: "15px",
                                        display: "flex",
                                        alignItems: "center",
                                      }}
                                    >
                                      <IconButton
                                        style={{ margin: "0 5px" }}
                                        onClick={() => {
                                          if (quantite > 0) {
                                            setQuantite(quantite - 1);
                                          }
                                        }}
                                      >
                                        <RemoveIcon />
                                      </IconButton>

                                      <span>{quantite}</span>

                                      <IconButton
                                        style={{ margin: "0 5px" }}
                                        onClick={() => setQuantite(quantite + 1)}
                                      >
                                        <AddIcon />
                                      </IconButton>
                                    </div>

                                    {/* If the item is non available, this will be displayed */}
                                    {!disponibilite && <p>Produit non disponible</p>}

                                    {disponibilite && (
                                      <Button
                                        disabled={quantite === 0}
                                        onClick={() => {
                                          verifySeletedDishes();
                                        }}
                                        variant="contained"
                                        color="secondary"
                                        className="card__item__commander-btn"
                                        endIcon={<AddShoppingCartIcon style={{ fontSize: "25px" }} />}
                                      >
                                        Ajouter au panier
                                      </Button>
                                    )}
                                  </Modal.Footer>
                                </Modal>


                            </div>
                        ))}

        </div>
    );
};
 export default CartePage;
