                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_ode_bio && data.type_ingredient === "ode_bio" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_limonaide_bio && data.type_ingredient === "est_limonaide_bio" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_milkshake && data.type_ingredient === "est_milkshake" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}
                                      {props.detail.est_smoothie && data.type_ingredient === "est_smoothie" ?
                                        <div className="sides-radioGroup" key={data.id}>
                                          <FormControlLabel
                                            value={data.nom}
                                            control={<Radio />}
                                            id={data.id}
                                            className="radio-choice__menu"
                                            onClick={() => {
                                              setError(false);
                                              //setTailleSelected(data);
                                            }}
                                          />
                                          <span>{data.nom}</span>
                                        </div>
                                      :null}

                                  </>

                                ))}

                            </RadioGroup>
                          </FormControl>
                          {error && (
                            <p className="error">Veuillez sélectionner au moins un élément</p>
                          )}
                          <div className="separation_ligne"> </div>
                      </Modal.Body>

                      )}

                    </>
                    :null}

{/* ---------------------------------Informations Importants---------------------------------------------------- */}
                <Modal.Body.Heading
                    style={{ marginTop: "20px" }}
                  >
                  Informations Importants
                  </Modal.Body.Heading>
                  <TextField
                    id="outlined-full-width"
                    style={{ marginTop: "1rem", marginBottom: "2rem" }}
                    placeholder="Informations importantes"
                    helperText="Indiquez ici toute information importante"
                    fullWidth
                    margin="normal"
                    rows={2}
                    variant="outlined"
                    onChange={handleCommentChange}
                  />


                  <Modal.Footer className="card__item__total__price">


                    <Modal.Body.Heading>
                      Prix Total
                      <span style={{ fontSize: "1rem", color: "black" }}>(TTC)</span>
                    </Modal.Body.Heading>
                    {/*<p style={{ fontSize: "1.3rem" }}>
                      {(quantite * prixTotal).toFixed(2)}€
                    </p>*/}
                  </Modal.Footer>

                    <Modal.Footer>
                        <div
                          style={{
                            marginRight: "15px",
                            display: "flex",
                            alignItems: "center",
                            backgroundColor: "var(--secondary-color)",
                            color: "white",
                          }}
                          className="produit-quantite"
                        >
                          <IconButton
                            style={{ margin: "0 5px" }}
                            onClick={() => {
                              if (quantite > 0) {
                                setQuantite(quantite - 1);
                              }
                            }}
                          >
                            <RemoveIcon />
                          </IconButton>

                          <span>{quantite}</span>

                          <IconButton
                            style={{ margin: "0 5px" }}
                            onClick={() => setQuantite(quantite + 1)}
                          >
                            <AddIcon />
                          </IconButton>
                        </div>

                      <div className="add-panier">

                          {/* If the item is non available, this will be displayed */}
                          {!props.detail.disponibilite && 
                            <div className="produit-non-dispo">Produit non disponible</div>}


                        {props.detail.disponibilite && (
                          <Button
                            disabled={quantite === 0}
                            onClick={() => {
                              //verifySeletedDishes();
                            }}
                            className="btn-add-produit"
                          >
                            {width < 600 
                              ? <AddShoppingCartIcon style={{ fontSize: "25px" }} /> 
                              : <>Ajouter au panier</>}
                          </Button>
                        )}
                        </div>

                      </Modal.Footer>
                  </Modal>

            </>
    );
};
export default ProduitDetail;
