import React from 'react';
import './styles.css';

import { CarteContent } from '../../../../data/site/carteContent';
import Grid from '@material-ui/core/Grid';


const CartePage = (props) => {
  const idMenu = props.idMenuPage;
  //console.log('abc' + idMenu);

    return(
        <div id="menu-page">

                    { CarteContent.map((idContent) => {
                      if(idContent.id === idMenu) {
                        return (
                          idContent.data.map((data) => (
                            <div className="menu-item">
                            <Grid container direction="row" >
                              <Grid container alignItems="center" justifyContent="center" direction="column"  xs={4}>
                                <img src={data.photo} alt={data.name}></img>
                              </Grid>
                              <Grid container justifyContent="center" alignItems="flex-start" direction="column" xs={8}>
                                <div className="menu-item-info">
                                  <p className="menu-item-titre">{data.name}</p>
                                  <p> {data.text} </p>
                                  <p><b>{data.prix} </b> </p>
                                </div>

                              </Grid>
                            </Grid>
                            </div>
                        ))
                        )
                      } else return null;
                    })}
        </div>
    );
};
 export default CartePage;
