import React, { useState, useEffect } from 'react';
import './styles.css';
import axios from 'axios';
import { CarteContent } from '../../../../data/site/carteContent';
import Grid from '@material-ui/core/Grid';
import { URL, sendrequest } from "../../../../middlewares/request";

const CartePageMobile = (props) => {
    const idMenu = props.idMenuPageMobile;
    const [produits, setProduits] = useState([]);

    const fetchProduit = () => {
      axios.get(URL + "gonak/produit/?categorie=" + idMenu).then((res) => {
        setProduits(res.data);
        console.log(res.data);
      });
    };

    useEffect(() => {
      fetchProduit();
      //console.log(produitsCarte);
    }, []);

    return (
        <div id="menu-page-mobile">

                    { produits.map((data) => (
                            <div className="menu-item-mobile">

                                <Grid container direction="row" xs={12} >
                                  <Grid container alignItems="center" justifyContent="center" direction="column"  xs={4}>
                                    <img src={data.image} alt={data.nom}></img>
                                  </Grid>
                                  <Grid container justifyContent="center" alignItems="flex-start" direction="column" xs={8}>
                                    <div className="menu-item-info-mobile">
                                      <p className="menu-item-titre-mobile">{data.nom}</p>
                                      <p> {data.description} </p>
                                      <p><b>{data.prix + "€"} </b> </p>
                                    </div>

                                  </Grid>
                                </Grid>

                            </div>
                        ))

                    }
        </div>
    );
};

export default CartePageMobile;
