                          style={{
                            marginRight: "15px",
                            display: "flex",
                            alignItems: "center",
                            backgroundColor: "var(--secondary-color)",
                            color: "white",
                          }}
                          className="produit-quantite"
                        >
                          <IconButton
                            style={{ margin: "0 5px" }}
                            onClick={() => {
                              if (quantite > 0) {
                                setQuantite(quantite - 1);
                              }
                            }}
                          >
                            <RemoveIcon />
                          </IconButton>

                          <span>{quantite}</span>

                          <IconButton
                            style={{ margin: "0 5px" }}
