import { useState } from "react";
import axios from "axios";
import "./card.css";
import { URL } from "../../../../middlewares/request"
import AddProductModal from "../MyModal/AddProductModal";

import { Button, IconButton } from "@material-ui/core";

import { splitPrix } from "../../../../utilities";

import Modal from "../MyModal/Modal";

const Card = ({
  nom,
  prix,
  id,
  description,
  categorie,
  ingredients,
  categories,
  disponibilite,
  image,
  sur_grill,
  sideDishes,
  setSideDishes

}) => {
  const [show, setShow] = useState(false);
  const [showButton, setShowButton] = useState(false);
  const [showProductModal, setShowProductModal] = useState(false);
  const product = {
    nom,
    prix,
    description,
    id,
    disponibilite,
    image,
    categorie,
  }

  const handleClose = (e) => {

    if (
      e.target.classList.contains("myModal__backdrop") ||
      e.target.classList.contains("myModal__modal__close-btn") ||
      e.target.parentNode.classList.contains("myModal__modal__close-btn") ||
      e.target.parentNode.parentNode.classList.contains(
        "myModal__modal__close-btn"
      )
    ) {
      setShow(false);
      setShowButton(false);
    }
  };

  const deleteProduct = async () => {
    await axios.delete(URL + "gonak/produit/" + id + "/")
      .then(() => console.log(`deleted`))
      .catch(error => console.error(error));
  }
  const updateProduct = () => {

  }



    ;

  return (

    // Depending on the availability or not of the item, the css style will vary, thanks to a different className 
    <div className="card__command__container">
      <div
        className={"card__item " + (!disponibilite ? "item_unavailable" : '')}
        onMouseEnter={() => setShowButton(true)}
        onMouseLeave={() => setShowButton(false)}
        onClick={() => setShow(true)}
      >
        <h1 className='card__item__heading'>{nom}</h1>
        <div className='card__item__image-container'>
          <img src={image} alt={nom} />
        </div>
        <div className='card__item__details'>
          <p>
            {/* {Prix[0]}€{Prix[1]} */}
            {splitPrix(prix)}
          </p>
        </div>
      </div>

      <div>
        <Modal showModal={show} setShowModal={setShow} handleClose={handleClose}>
          <Modal.Header>
            <h1>{nom}</h1>
            <img src={image} alt={nom} />
            <p>{splitPrix(prix)}</p>
          </Modal.Header>
          <Modal.Body>
            <Modal.Body.Heading>Description</Modal.Body.Heading>
            <p>{description}</p>
          </Modal.Body>
          <Modal.Footer>
            <div
              style={{
                marginRight: "15px",
                width: "100%",
                display: "flex",
                justifyContent: "space-between",
                border: "0.7px solid black",

              }}>
              <Button onClick={deleteProduct}>supprimer</Button>
              <Button onClick={setShowProductModal}>Modifier</Button>
              <AddProductModal productToUpdate={product} categorieId={categorie} show={showProductModal} setShow={setShowProductModal} ></AddProductModal>
            </div>
          </Modal.Footer>
        </Modal>
      </div>
    </div>
  );
};

const ajoutBtn = {
  from: {
    opacity: 0,
    y: 20,
  },
  to: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.3,
    },
  },

  exit: {
    opacity: 0,
    y: 20,
    transition: {
      duration: 0.3,
    },
  },
};

export default Card;
