import React, {useState, useEffect} from 'react'
import './styles.css';
import axios from "axios";
import { URL, sendrequest } from "../../../../middlewares/request";
//import { CarteContent } from '../../../../data/site/carteContent';
import Grid from '@material-ui/core/Grid';
import ShowMoreText from "react-show-more-text";
import { Button, IconButton, TextField } from "@material-ui/core";
import {
  FormControlLabel,
  FormControl,
  RadioGroup,
  Radio,
  Checkbox,
} from "@material-ui/core";
import FormGroup from "@material-ui/core/FormGroup";
import AddShoppingCartIcon from "@material-ui/icons/AddShoppingCart";
import { splitPrix } from "../../../../utilities";

import Modal from "../MyModal/Modal";
import RemoveIcon from "@material-ui/icons/Remove";
import AddIcon from "@material-ui/icons/Add";


const ProduitDetail = (props) => {
  const [quantite, setQuantite] = useState(1);
  const [show, setShow] = useState(false);
  const [error, setError] = useState(false);

  const [tailleSelected, setTailleSelected] = useState(null); // type de prix pour les produits à plusieurs prix
  const [sandPlatSelected, setSandPlatSelected] = useState(null);
  const [entreeSelected, setEntreeSelected] = useState(null);
  const [dessertSelected, setDessertSelected] = useState(null);


  console.log(props.detail);


    const handleClose = (e) => {
        if (
          e.target.classList.contains("myModal__backdrop") ||
          e.target.classList.contains("myModal__modal__close-btn") ||
          e.target.parentNode.classList.contains("myModal__modal__close-btn") ||
          e.target.parentNode.parentNode.classList.contains(
            "myModal__modal__close-btn"
          )
        ) {
          setShow(false);
      }
    };

    const handleTailleSelected = (event) => { // ++ prix
      setTailleSelected(event.target.value);
      setError(false);
    };

    const handleSandPlatSelected = (event) => {
      setSandPlatSelected(event.target.value);
      setError(false);
    };

    const handleEntreeSelected = (event) => {
      setEntreeSelected(event.target.value);
      setError(false);
    };

    const handleDessertSelected = (event) => {
      setDessertSelected(event.target.value);
      setError(false);
    };

    const showMultiplePrice = (data) => {

      if(data.categorie === 1 && data.prix === 0){
        return (<p><b>{"À partir de " + data.prix_gourmande + " €"} </b> </p>)
      }
      else if(data.categorie === 2 && data.prix === 0){
        return (<p><b>{"À partir de " + data.pieces_x5 + " €"} </b> </p>)
      }
      else if(data.categorie === 4 && data.prix === 0){
        return (<p><b>{"À partir de " + data.sans_accompagnement + " €"} </b> </p>)
      }
      else if(data.categorie === 12 && data.prix === 0){
        return (<p><b>{"À partir de " + data.moyen + " €"} </b> </p>)
      }
      else{
        return (<p><b>{data.prix + " €"} </b> </p>)
      }
    }

    const showMultipleDishes = (data) => {

      if(data.categorie === 1 && data.prix === 0){
        return (
          <>
            <Modal.Body>
                <Modal.Body.Heading
                  style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                >
                  Type de formule
                </Modal.Body.Heading>
                <FormControl component="fieldset">
                  <RadioGroup
                    name="type_formule"
                    value={tailleSelected}
                    onChange={handleTailleSelected}
                  >

                          <div className="sides-radioGroup" key={1}>
                            <FormControlLabel
                              value="gourmande"
                              control={<Radio />}
                              id={1}
                              className="radio-choice__menu"
                              onClick={() => {
                                setError(false);
                                //setTailleSelected(data);
                              }}
                            />
                            <span>{"Gourmande (13€)"}</span>
                          </div>
                          <div className="sides-radioGroup" key={2}>
                            <FormControlLabel
                              value="intense"
                              control={<Radio />}
                              id={2}
                              className="radio-choice__menu"
                              onClick={() => {
                                setError(false);
                                //setTailleSelected(data);
                              }}
                            />
                            <span>{"Intense (16€)"}</span>
                          </div>

                  </RadioGroup>
                </FormControl>
                {error && (
                  <p className="error">Veuillez sélectionner au moins un élément</p>
                )}
                <div className="separation_ligne"> </div>
              </Modal.Body>

              {tailleSelected === "intense" ? (
                <>
                  <Modal.Body>
                        <Modal.Body.Heading
                          style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                        >
                         Entrée
                        </Modal.Body.Heading>
                        <FormControl component="fieldset">
                          <RadioGroup
                            name="entree"
                            value={entreeSelected}
                            onChange={handleEntreeSelected}
                          >

                            {props.entrees?.map((data) => (

                                  <div className="sides-radioGroup" key={data.id}>
                                    <FormControlLabel
                                      value={data.nom}
                                      control={<Radio />}
                                      id={data.id}
                                      className="radio-choice__menu"
                                      onClick={() => {
                                        setError(false);
                                        //setTailleSelected(data);
                                      }}
                                    />
                                    <span>{data.nom}</span>
                                  </div>

                              ))}

                          </RadioGroup>
                        </FormControl>
                        {error && (
                          <p className="error">Veuillez sélectionner au moins un élément</p>
                        )}
                        <div className="separation_ligne"> </div>
                    </Modal.Body>
                  </>
                  ):("")}



                    <Modal.Body>
                        <Modal.Body.Heading
                          style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                        >
                        {props.detail.nom.includes('sandwich') ? ("Sandwich/wraps") : ("Salades/pâtes")}
                        </Modal.Body.Heading>
                        <FormControl component="fieldset">
                          <RadioGroup
                            name="plats"
                            value={sandPlatSelected}
                            onChange={handleSandPlatSelected}
                          >

                          {props.detail.nom.includes('sandwich') ? (
                            <>

                              {props.wraps?.map((data) => (

                                  <div className="sides-radioGroup" key={data.id}>
                                    <FormControlLabel
                                      value={data.nom}
                                      control={<Radio />}
                                      id={data.id}
                                      className="radio-choice__menu"
                                      onClick={() => {
                                        setError(false);
                                        //setTailleSelected(data);
                                      }}
                                    />
                                    <span>{data.nom}</span>
                                  </div>

                              ))}
                              {props.sandwichs?.map((data) => (

                                  <div className="sides-radioGroup" key={data.id}>
                                    <FormControlLabel
                                      value={data.nom}
                                      control={<Radio />}
                                      id={data.id}
                                      className="radio-choice__menu"
                                      onClick={() => {
                                        setError(false);
                                        //setTailleSelected(data);
                                      }}
                                    />
                                    <span>{data.nom}</span>
                                  </div>

                              ))}
                              </>
                            ) : (
                              <>
                                  {props.salades?.map((data) => (

                                      <div className="sides-radioGroup" key={data.id}>
                                        <FormControlLabel
                                          value={data.nom}
                                          control={<Radio />}
                                          id={data.id}
                                          className="radio-choice__menu"
                                          onClick={() => {
                                            setError(false);
                                            //setTailleSelected(data);
                                          }}
                                        />
                                        <span>{data.nom}</span>
                                      </div>

                                  ))}
                                  {props.pates?.map((data) => (

                                      <div className="sides-radioGroup" key={data.id}>
                                        <FormControlLabel
                                          value={data.nom}
                                          control={<Radio />}
                                          id={data.id}
                                          className="radio-choice__menu"
                                          onClick={() => {
                                            setError(false);
                                            //setTailleSelected(data);
                                          }}
                                        />
                                        <span>{data.nom}</span>
                                      </div>

                                  ))}
                                </>
                            )}
                          </RadioGroup>
                        </FormControl>
                        {error && (
                          <p className="error">Veuillez sélectionner au moins un élément</p>
                        )}
                        <div className="separation_ligne"> </div>
                    </Modal.Body>

                  <Modal.Body>
                        <Modal.Body.Heading
                          style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}
                        >
                         Dessert
                        </Modal.Body.Heading>
                        <FormControl component="fieldset">
                          <RadioGroup
                            name="dessert"
                            value={dessertSelected}
                            onChange={handleDessertSelected}
                          >

                            {props.desserts?.map((data) => (

                                  <div className="sides-radioGroup" key={data.id}>
                                    <FormControlLabel
                                      value={data.nom}
                                      control={<Radio />}
                                      id={data.id}
                                      className="radio-choice__menu"
                                      onClick={() => {
                                        setError(false);
                                        //setTailleSelected(data);
                                      }}
                                    />
                                    <span>{data.nom}</span>
                                  </div>

                              ))}

                          </RadioGroup>
                        </FormControl>
                        {error && (
                          <p className="error">Veuillez sélectionner au moins un élément</p>
                        )}
                        <div className="separation_ligne"> </div>
                    </Modal.Body>


          </>
        )
      }
      else if(data.categorie === 2 && data.prix === 0){
        return (<p><b>{"À partir de " + data.pieces_x5 + " €"} </b> </p>)
      }
      else if(data.categorie === 4 && data.prix === 0){
        return (<p><b>{"À partir de " + data.sans_accompagnement + " €"} </b> </p>)
      }
      else if(data.categorie === 12 && data.prix === 0){
        return (<p><b>{"À partir de " + data.moyen + " €"} </b> </p>)
      }
      else{
        return ("")
      }
    }



    return(

          <>
              <div className="menu-item" onClick={() => setShow(true)}>
                <Grid container direction="column" justifyContent="center" alignItems="center">
                    {/*<img src={props.detail.image} alt={props.detail.nom}></img>*/}
                    <p className="menu-item-img"></p>

                    <div className="menu-item-info">
                      <p className="menu-item-titre">{props.detail.nom}</p><br/>
                      <ShowMoreText
                            /* Default options */
                            lines={2}
                            more=""
                            less=""
                            className="content-css"
                            anchorClass="my-anchor-css-class"
                            /*onClick={this.executeOnClick}*/
                            expanded={false}
                            width={280}
                            truncatedEndingComponent={"... "}
                            className="menu-item-text"
                        >
                            {props.detail.description}
                        </ShowMoreText><br/>
                      {showMultiplePrice(props.detail)}<br/>
                    </div>
                  </Grid>
                </div>

                  <Modal
                    showModal={show}
                    setShowModal={setShow}
                    handleClose={handleClose}
                  >
                    <Modal.Body>
                      <img src={props.detail.image} alt={props.detail.nom} />
                      <Modal.Body.Heading>{props.detail.nom}</Modal.Body.Heading>
                      {showMultiplePrice(props.detail)}
                    </Modal.Body>

                    <Modal.Body>
                      <Modal.Body.Heading>Description</Modal.Body.Heading>
                      <p>{props.detail.description}</p>
                    </Modal.Body>

                    {showMultipleDishes(props.detail)}


                    <Modal.Footer>
                        <div
                          style={{
                            marginRight: "15px",
                            display: "flex",
                            alignItems: "center",
                          }}
                        >
                          <IconButton
                            style={{ margin: "0 5px" }}
                            onClick={() => {
                              if (quantite > 0) {
                                setQuantite(quantite - 1);
                              }
                            }}
                          >
                            <RemoveIcon />
                          </IconButton>

                          <span>{quantite}</span>

                          <IconButton
                            style={{ margin: "0 5px" }}
                            onClick={() => setQuantite(quantite + 1)}
                          >
                            <AddIcon />
                          </IconButton>
                        </div>

                        {/* If the item is non available, this will be displayed */}
                        {!props.detail.disponibilite && <p>Produit non disponible</p>}

                        {props.detail.disponibilite && (
                          <Button
                            disabled={quantite === 0}
                            onClick={() => {
                              //verifySeletedDishes();
                            }}
                            variant="contained"
                            color="secondary"
                            className="card__item__commander-btn"
                            endIcon={<AddShoppingCartIcon style={{ fontSize: "25px" }} />}
                          >
                            Ajouter au panier
                          </Button>
                        )}
                      </Modal.Footer>
                  </Modal>

            </>
    );
};
export default ProduitDetail;
