import React from 'react';

import './base.css';
import './styles.css';

import {
    Route, Switch, useRouteMatch
  } from 'react-router-dom';

import Acceuil from './Acceuil/Acceuil';
import Header from './Header';
import Footer from './Footer';
import Gallerie from './Gallerie';
import Carte from './Carte';
import ContactForm from './ContactForm';
import Panier from './Panier/Panier';
import Paiement from './Panier/Paiement';

//Stripe
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
const publicKey="pk_test_51JDrRDIbpxt7EjD8J1YiiQxZK01GsskEId9DBtuR3FKmV5wHkMjCq1Odhqxt02g8D4g4R9oe60HA92vl537fJmra007tMqc8XR";



const Site = () => {
  const { url } = useRouteMatch();

  return (

    <div className="site">
          <Header />

            <Switch>
              <Route
                exact
                path={`${url}/`}
              >
                  <Acceuil />
              </Route>

              <Route
                exact
                path={`${url}/carte`}    
              >
                  <Carte />
              </Route>

              <Route
                exact
                path={`${url}/contact-form`}    
              >
                  <ContactForm />
              </Route>

              <Route
                exact
                path={`${url}/gallerie`}
              >
                  <Gallerie />
              </Route>

              <Route
                exact
                path={`${url}/panier`}
              >
                  <Panier />
              </Route>

              <Route
                exact
                path={`${url}/paiement`}
              >
              <Elements stripe={loadStripe(publicKey)}>
                  <Paiement />
              </Elements>
              </Route>

          </Switch>

        <Footer />
      </div>


  )
};
export default Site;
