import axios from "axios";
import { useState, useEffect } from "react";
import { URL } from "../../../../middlewares/request"
import { Button, IconButton, TextField } from "@material-ui/core";
import {
  FormControlLabel,
  FormControl,
  RadioGroup,
  Radio,
  Checkbox,
  OutlinedInput,
} from "@material-ui/core";
import FormGroup from "@material-ui/core/FormGroup";
/*import "./modal.css";*/
import Modal from "../MyModal/Modal";

export default function ({
    categorieId,
    show,
    setShow,
    productToUpdate
}) {
    const initialProduct = {
        nom: productToUpdate?.nom ? productToUpdate.nom : "",
        prix: productToUpdate ? productToUpdate.prix : "",
        image: productToUpdate ? productToUpdate.image : null,
        description: productToUpdate ? productToUpdate.description : "",
        categorie: categorieId,
        disponibilite: productToUpdate ? productToUpdate.disponibilte : ""
    }
    const [product, setProduct] = useState(initialProduct);
    const [nom, setNom] = useState(productToUpdate?.nom);
    const [imageImported, setImageImported] = useState(false);

        console.log(product);

        console.log(productToUpdate);



    /*const test = () =>{
        if(productToUpdate?.nom)
        {
            console.log("TEST3");
            console.log(productToUpdate.nom);
            const initialProduct = {
                nom: productToUpdate ? productToUpdate.nom : "",
                prix: productToUpdate ? productToUpdate.prix : "",
                image: productToUpdate ? productToUpdate.image : null,
                description: productToUpdate ? productToUpdate.description : "",
                categorie: categorieId,
                disponibilite: productToUpdate ? productToUpdate.disponibilte : ""
            }

            setProduct(initialProduct);
            console.log("reussite");
            console.log(product);
        }
        else{
            const initialProduct = {
                nom: productToUpdate?.nom ? productToUpdate.nom : "",
                prix: productToUpdate?.prix ? productToUpdate.prix : "",
                image: productToUpdate?.image ? productToUpdate.image : null,
                description: productToUpdate?.description ? productToUpdate.description : "",
                categorie: categorieId,
                disponibilite: productToUpdate?.disponibilite ? productToUpdate.disponibilte : ""
            }

            setProduct(initialProduct);
            console.log("échec");
            console.log(product);
        }
    }

    useEffect(() => {
      test();

    }, []);*/

    const handleClose = (e) => {
        if (
            e.target.classList.contains("myModal__backdrop") ||
            e.target.classList.contains("myModal__modal__close-btn") ||
            e.target.parentNode.classList.contains("myModal__modal__close-btn") ||
            e.target.parentNode.parentNode.classList.contains(
                "myModal__modal__close-btn"
            )
        ) {

            setShow(false);
        }
    };

    const addProduct = async (e) => {
        e.preventDefault();
        console.log(`form_data`, product)

        let form_data = new FormData();
        form_data.append("nom", product.nom);
        form_data.append("description", product.description);
        /*if (product.image !== null)
            form_data.append("image", product.image, product.image.name);*/
        form_data.append("prix", product.prix);
        form_data.append("categorie", categorieId);
        form_data.append("dsponibilite", product.disponibilite);
        console.log(`from_data`, form_data)
        let url = URL + "gonak/produit/";
        axios
            .post(url, form_data, {
                headers: {
                    "content-type": "multipart/form-data",
                },
            })
            .then((res) => {
                console.log(`added susscessfully`)
            })
            .catch((error) => {
                console.error(error)
            });

        //window.location.reload(false);
    }

    const deleteProduct = async () => {
        await axios.delete(URL + "gonak/produit/" + productToUpdate.id + "/")
          .then(() => console.log(`deleted`))
          .catch(error => console.error(error));

          window.location.reload(false);
      }

    const updateProduct = (e) => {
        e.preventDefault();
        console.log(`form_data`, product)

        let form_data = new FormData();
        form_data.append("nom", product.nom);
        form_data.append("description", product.description);
        if (product.image !== null)
            form_data.append("image", product.image, product.image.name);
        form_data.append("prix", product.prix);
        form_data.append("categorie", product.categorie);
        form_data.append("dsponibilite", product.disponibilite);
        let url = URL + "gonak/produit/" + productToUpdate.id + "/";
        axios
            .put(url, form_data, {
                headers: {
                    "content-type": "multipart/form-data",
                },
            })
            .then((res) => {
                console.log(`updated susscessfully`)
            })
            .catch((error) => {
                console.error(error)
            });


        window.location.reload(false); 
    }
    const inputChange = (e) => {
        setProduct({ ...product, [e.target.name]: e.target.value })
    }

    const handleChange = (e) => {
        setProduct({ ...product, [e.target.name]: e.target.checked })
    }

    console.log("TEST3");
    console.log(product);

    return (

        <Modal showModal={show} setShowModal={setShow} handleClose={handleClose}>
            <Modal.Header>
                {productToUpdate ?
                    <h1>Modifier un Produit</h1>
                    :
                    <h1>Ajouter un Produit</h1>
                }
            </Modal.Header>

            <Modal.Body.Heading
                style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}>
                Informations du produit
            </Modal.Body.Heading>

            <Modal.Body>
            <FormControl >
                <div className="basic-input">
                    <TextField
                        id="input-nom"
                        label="nom"
                        variant="outlined"
                        name="nom"
                        size="small"
                        value={nom}
                        onChange={inputChange}
                    />
                </div>
                <div className="basic-input">
                <TextField
                    id="input-description"
                    label="description"
                    variant="outlined"
                    multiline
                    name="description"
                    size="small"
                    value={product?.description}
                    onChange={inputChange}

                />
                </div>
                <div className="basic-input">
                <TextField
                    id="image"
                    type="file"
                    variant="outlined"
                    size="small"
                    name="image"
                    onChange={(event) => {
                        const fileUploaded = event.target.files[0]
                        setProduct({ ...product, image: fileUploaded });
                        setImageImported(true);
                    }
                    }
                />
                </div>
                </FormControl >
                <Modal.Body.Heading
                    style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}>
                    Prix du produit
                </Modal.Body.Heading>
                <FormControl >
                <div className="basic-input">
                <TextField
                    id="input-prix"
                    label="prix"
                    variant="outlined"
                    size="small"
                    name="prix"
                    value={product?.prix}
                    onChange={inputChange}

                />
                </div>
                </FormControl >

                <Modal.Body.Heading
                    style={{ marginBottom: "1.5rem", marginTop: "1.3rem" }}>
                    Disponibilité du produit
                </Modal.Body.Heading>
                <FormControl component="fieldset">
                    <FormGroup>

                        <div className="sides-radioGroup" key={product?.id}>
                          <FormControlLabel
                            value={product?.nom}
                            control={<Checkbox onChange={handleChange} />}
                            id={product?.id}
                            className="radio-choice__menu"

                          />
                          {/*<span>{product.nom}</span>*/}
                        </div>


                    </FormGroup>
                </FormControl>


            </Modal.Body>

            <Modal.Footer>
                {productToUpdate ?
                    <div
                      style={{
                        marginRight: "15px",
                        width: "100%",
                        display: "flex",
                        justifyContent: "space-between",
                        border: "0.7px solid black",

                      }}>
                        <Button onClick={updateProduct}>Modifier</Button>
                        <Button onClick={deleteProduct}>supprimer</Button>
                    </div>
                    :
                    <Button onClick={addProduct}> Ajouter </Button>
                }
            </Modal.Footer>
        </Modal>
    );
};
