import React ,{useState,useEffect} from 'react';
import axios from "axios"
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import {useSelector} from 'react-redux';
import { useHistory } from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import { URL } from "../../../middlewares/request";

// import jwt_decode from "jwt-decode";


const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(5),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor:'white',
    padding:10,
    borderRadius:'3%',
    boxShadow:' 0px 0px 5px 1px',
    //opacity:'80%',
    border :'solid #04295D'


  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: "#04295D",
  },
  form: {
    width:'100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),

  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    backgroundColor:"#04295D",

    //backgroundColor:theme.palette.secondary,
  },
  disable: {
    backgroundColor:"#04295D",

  },
  error:{
    color:'red',
    fontWeight:'bold',
    fontSize:13,
    textAlign:'center'
  }
  ,
  Container:{
    marginTop:100,
  },
  input:{
     fontWeight:'bold'
  },
  main:{
    height : 800 ,   
  },
  logo:{
    width:150,
    height:150,
    marginTop:"10px",
    marginBottom:"10px"
  },
  logoContainer:{
    width:"100%",
    backgroundColor:"#04295D",
    textAlign: "center"

  }
}));
const  Login =() =>{
    const classes = useStyles();
    const [loginState,setloginState]=useState({username: "",password: "",errors: " "});
    const security=useSelector(state=>state.security);
    const errors=useSelector(state=>state.errors);
    const [shoudDisableSignIn,setShoudDisableSignIn]=useState(false);
    const history =useHistory();

    const authenticate = async(loginRequest)=>{
        await axios({
            url:`${URL}owners/token/`,
            method:'post',
            //headers: {'Access-Control-Allow-Origin': '*'},
            data: loginRequest
            }).
            then(res=>{
                // extract token from res.data
                const { access } = res.data;
                // store the token in the localStorage
                localStorage.setItem("jwtToken", access);
                // set our token in header ***
                // decode token on React
                // // const decoded = jwt_decode(token);
                // dispatch to our securityReducer
                setShoudDisableSignIn(false);
                history.push("/admin")

            }).catch(err=>{
                setloginState({...loginState,errors:"Email ou mot de passe incorrecte"})
                setShoudDisableSignIn(false);
            })
    }

    const onChange =(e)=>{
      setloginState({ ...loginState,[e.target.name]: e.target.value });
    }
    const onSubmit =(e)=> {
      e.preventDefault();
      setShoudDisableSignIn(true);
      const loginRequest = {
        username: loginState.username,
        password: loginState.password
      };
      authenticate(loginRequest);
    }
  return (
    <div className={classes.main}>
    <div className={classes.logoContainer}><img src ="logo_petit_m.png" className={classes.logo} /></div>
    <Container component="main" maxWidth="xs"  >
      <CssBaseline />
      <div className={classes.paper}  >
        <Avatar className={classes.avatar}/>
        <Typography component="h1" variant="h5">
            Connexion
        </Typography>
        <form className={classes.form} noValidate onSubmit={onSubmit}>
          <span style={{fontWeight:"bold"}}>Email :</span>
         <input
            variant="filled"
            fullWidth
            id="dashboard-outlined-basic"
            type="email"
            name="username"
            placeholder="Adresse mail"
            onChange={onChange}
            style={{width:"60%"}}
          /><br/>
          <span  style={{fontWeight:"bold"}}>Mot de passe :</span> 
          <input
            type="password"
            name="password"
            id="dashboard-outlined-basic"
            placeholder="Mot de passe"
            autoComplete="current-password"
            style={{width:"60%"}}
            onChange={onChange}
          />
          {loginState.errors!=="" && <p className={classes.error}>{loginState.errors} </p>}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="secondary"
            className={!shoudDisableSignIn?classes.submit:classes.disable}
            disabled={shoudDisableSignIn}
          >
             {shoudDisableSignIn?<div style={{color:"brown"}}>Connexion au serveur ..... </div>:'Connexion '}
          </Button>
        </form>
      </div>
    </Container>
    </div>
  );
}
export default Login;
